import React, { useState, useEffect } from 'react';

function FirstFrame() {
  const [frameUrl, setFrameUrl] = useState('');
  const [ocrResults, setOcrResults] = useState([]);
  const [videoUrl, setVideoUrl] = useState('');
  const [isLoadingFrame, setIsLoadingFrame] = useState(false);
  const [isPerformingOCR, setIsPerformingOCR] = useState(false);

  useEffect(() => {
    async function fetchStoredUrl() {
      const response = await fetch('http://localhost:3001/video');
      if (response.ok) {
        const data = await response.json();
        if (data.url) {
          setVideoUrl(data.url);
        }
      }
    }

    fetchStoredUrl();
  }, []);

  const handleFetchFirstFrame = async (url) => {
    setIsLoadingFrame(true);
    const response = await fetch(`/video/first-frame?url=${encodeURIComponent(url)}`);
    if (response.ok) {
      const blob = await response.blob();
      const imageUrl = URL.createObjectURL(blob);
      setFrameUrl(imageUrl);
    } else {
      alert('Failed to fetch the first frame.');
    }
    setIsLoadingFrame(false);
  };

  const performOCR = async () => {
    if (!frameUrl) {
      alert('No image to perform OCR on.');
      return;
    }
  
    setIsPerformingOCR(true);
  
    // Fetching the image data from the blob URL
    const response = await fetch(frameUrl);
    const blob = await response.blob();
    const reader = new FileReader();
  
    reader.readAsDataURL(blob);
    reader.onloadend = async () => {
      const base64data = reader.result;
  
      // Now send this base64data to your backend
      const ocrResponse = await fetch('/performOCR', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ imageData: base64data }),
      });
  
      if (ocrResponse.ok) {
        const data = await ocrResponse.json();
        setOcrResults(data);
      } else {
        alert('Failed to perform OCR.');
      }
  
      setIsPerformingOCR(false);
    };
  };
  

  return (
    <div className='flex flex-col items-center justify-center mt-12'>
      <button
        className='border-2 border-black py-2 px-3 m-2 roboto-medium rounded-md bg-white tracking-wider'
        onClick={() => handleFetchFirstFrame(videoUrl)}
        disabled={isLoadingFrame}
      >
        {isLoadingFrame ? 'Fetching Frame...' : 'Get First Frame as Image'}
      </button>

      {frameUrl && (
        <>
          <img className='w-1/4' src={frameUrl} alt="First frame of the video" />
          <a
            className='border-2 border-black py-1 px-2 m-2 roboto-medium rounded-md bg-white tracking-wider'
            href={frameUrl}
            download="first-frame.jpg"
          >
            Download First Frame
          </a>
          {!isPerformingOCR && (
            <button
              className='border-2 border-black py-1 px-2 m-2 roboto-medium rounded-md bg-white tracking-wider'
              onClick={performOCR}
            >
              Perform OCR
            </button>
          )}
        </>
      )}

      {isPerformingOCR && <p>Performing OCR...</p>}

      {ocrResults.length > 0 && (
        <div>
          <h3>OCR Results:</h3>
          {ocrResults.map((result, index) => (
            <p key={index}>{result.description}</p>
          ))}
        </div>
      )}
    </div>
  );
}

export default FirstFrame;
