import React, { useState, useEffect } from 'react';



function AudioPlayer() {
  const [audioUrl, setAudioUrl] = useState('');

  useEffect(() => {
    // Directly set the audio URL to the GCS file location
    // Ensure the bucket is public or set up for authenticated access
    const gcsAudioUrl = 'https://storage.googleapis.com/marcosargiottitask/audio-buffer.mp3';
    setAudioUrl(gcsAudioUrl);
  }, []);

  function Reprocess(){
    const gcsAudioUrl = 'https://storage.googleapis.com/marcosargiottitask/audio-buffer.mp3';
    setAudioUrl(gcsAudioUrl);
    console.log("The url was refreshed")
  }

  const handleDownload = (downloadUrl) => {
    fetch(downloadUrl).then(response => {
      response.blob().then(blob => {
        let url = window.URL.createObjectURL(blob);
        let a = document.createElement('a');
        a.href = url;
        a.download = 'clip.mp3';
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
      });
    });
  };

  return (
    <>
    <div className="flex flex-row items-center justify-center mt-8">
      {audioUrl && (
        <div className="flex flex-row items-center justify-center">
          <audio controls src={audioUrl}>
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
      <button onClick={Reprocess} className="border-2 border-black py-2 px-3 m-2 roboto-medium rounded-md bg-white tracking-wider">
        Reprocess the audio
      </button>
    </div>
    <p className='text-white text-center'>To download the audio clip press on the options button of the player</p>
    </>
  );
}

export default AudioPlayer;
