import React, { useState, useEffect } from 'react';



function AudioPlayer() {
  const [audioUrl, setAudioUrl] = useState('');

  useEffect(() => {
    // Directly set the audio URL to the GCS file location
    // Ensure the bucket is public or set up for authenticated access
    const gcsAudioUrl = 'https://storage.googleapis.com/marcosargiottitask/audio-buffer.mp3';
    setAudioUrl(gcsAudioUrl);
  }, []);

  function Reprocess(){
    const gcsAudioUrl = 'https://storage.googleapis.com/marcosargiottitask/audio-buffer.mp3';
    setAudioUrl(gcsAudioUrl);
    console.log("The url was refreshed")
  }

  const handleDownload = (downloadUrl) => {
    fetch(downloadUrl).then(response => {
      response.blob().then(blob => {
        let url = window.URL.createObjectURL(blob);
        let a = document.createElement('a');
        a.href = url;
        a.download = 'clip.mp3';
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
      });
    });
  };

  return (
    <div className="flex flex-col items-center justify-center mt-8">
      <button onClick={Reprocess} className="bg-white h-28 p-3 m-3">
        Reprocess the audio
      </button>
      {audioUrl && (
        <div className="flex flex-col items-center justify-center">
          <audio controls src={audioUrl}>
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
    </div>
  );
}

export default AudioPlayer;
