import React, { useState } from 'react';

function VideoContainer() {
  const [url, setUrl] = useState('');
    const [status, setStatus] = useState('');
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/video', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url }),
      });
      if (response.ok) {
        console.log('URL submitted successfully');
        setStatus('URL Submitted succesfully')
      } else {
        console.error('Submission failed');
        setStatus('Submission failed')
      }
    } catch (error) {
      console.error('Error submitting the URL:', error);
      setStatus('Error submitting the URL:', error)
    }
  };

  return (
    <>
      <div className="h-24 flex items-center justify-center">
        <form onSubmit={handleSubmit} className="flex items-center">
          <label className="text-3xl m-1">
            Video URL:
            <input
              type="text"
              className="border border-black m-1 text-lg h-[40px]"
              value={url}
              placeholder="URL of your .MP4 Video"
              onChange={(e) => setUrl(e.target.value)}
            />
          </label>
          <button className="border p-1 m-2" type="submit">
            Submit
          </button>
        </form>
      </div>
      <div className="flex items-center">
        <p className='flex items-center'>{status}</p>
      </div>
    </>
  );
}

export default VideoContainer;
