import React, { useState, useEffect } from 'react';

function FirstFrame() {
  const [frameUrl, setFrameUrl] = useState('');
  const [videoUrl, setVideoUrl] = useState(''); // You need to have the video URL state if it's dynamic.

  useEffect(() => {
    const fetchStoredUrl = async () => {
      const response = await fetch('http://localhost:3001/video');
      if (response.ok) {
        const data = await response.json();
        if (data.url) {
          setVideoUrl(data.url);
        }
      }
    };

    fetchStoredUrl();
  }, []);

  const handleFetchFirstFrame = async (url) => {
    const response = await fetch(`/video/first-frame?url=${encodeURIComponent(url)}`);
    if (response.ok) {
      const blob = await response.blob();
      const url = URL.createObjectURL(blob);
      setFrameUrl(url);
    } else {
      alert('Failed to fetch the first frame.');
    }
  };

  return (
    <div className='flex flex-col items-center justify-center mt-12'>
        {/* Removed the immediate function call and replaced with a function that calls handleFetchFirstFrame with videoUrl */}
        <button className='border-2 border-black py-2 px-3 m-2 roboto-medium rounded-md bg-white tracking-wider' onClick={() => handleFetchFirstFrame(videoUrl)}>Get first frame as an image</button>
      {frameUrl && (
        <>
          <img className='w-1/4' src={frameUrl} alt="First frame of the video" />
          <a href={frameUrl} download="first-frame.jpg">Download First Frame .jpg</a>
        </>
      )}
    </div>
  );
}

export default FirstFrame;
